def normalize_pem(string_pem):
    pem = ""
    header = "-----BEGIN CERTIFICATE-----\n"
    pem += header
    footer = "-----END CERTIFICATE-----\n"
    for i in range(0,len(string_pem)):
        if i%64 == 0 and i != 0:
            pem += "\n"+string_pem[i]
        else:
            pem+=string_pem[i]
    pem += "\n"
    pem += footer
    return pem

